mapper.select:
select

from t_coupon
        where deleted = false

    AND ((start_time > #{startTime}
            AND start_time < #{endTime}) or (end_time > #{startTime} AND end_time < #{endTime}))



    AND des like #{bind}


    AND status = #{status}


    AND code = #{code}

order by create_at desc
limit #{offset},#{limit}

select count(id)
from t_coupon
        where deleted = false

    AND ((start_time > #{startTime}
            AND start_time < #{endTime}) or (end_time > #{startTime} AND end_time < #{endTime}))



    AND des like #{bind}


    AND status = #{status}


    AND code = #{code}
