mapper.select:
select

from t_order
where user_id = #{userId,jdbcType=BIGINT}
  and id = #{id,jdbcType=BIGINT}

select

from t_order
where `status` = #{status,jdbcType=TINYINT}
  and create_at + #{cancelDuration,jdbcType=BIGINT} < #{now,jdbcType=BIGINT}
order by create_at

select

from t_order
        where deleted = false

    AND user_id = #{userId}


    AND status = #{status}

order by create_at desc
limit #{offset},#{limit}

select

from t_order
where deleted = false
  AND create_at < #{expired}
  AND status = #{status}
order by create_at desc

select

from t_order
        where deleted = false

    AND create_at > #{startTime}


    AND create_at < #{endTime}


    AND order_type = #{orderType}


    AND order_num = #{orderNum}


    AND user_id = #{userId}


    AND status = #{status}

order by create_at desc
limit #{offset},#{limit}

select count(id)
from t_order
        where deleted = false

    AND create_at > #{startTime}


    AND create_at < #{endTime}


    AND order_type = #{orderType}


    AND order_num = #{orderNum}


    AND user_id = #{userId}


    AND status = #{status}
